import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import {useFetch} from "../../hooks/useFetch";
import "../Tela.css"
import { useState , useEffect } from "react";

const Form2 = (props) => {   
 
    let checados = document.getElementById('flexCheckDefault')['form-check-input'];
    let interesses = [];
    for( i=0; i< checados.length; i++){
        if(checados[i].checked){
            let tema = checados[i].value;
            interesses.push(tema);
        }
    }
    props.interesses(interesses);

    return <>
                    <h2>Escolha seus interesses</h2>
                    <Form.Group className="mb-2" >
                    <div className="form-check">
                       <ul>
                        {props.temas && props.temas.map((tema) => (
                            <li key={tema.id_tema}>
                                <input className="form-check-input" type="checkbox" value={props.tema} id="flexCheckDefault"/>
                                <label className="form-check-label" htmlFor="flexCheckDefault">
                                    {tema.tema}
                                </label>
                            </li>
                        ))}
                        </ul>
                        { }
                    </div>
                    </Form.Group>

                    <br/>
                    <Button variant="primary" type="submit" className="px-5 mb-3 mt-3">
                    Enviar
                    </Button>   
    </>
}

export default Form2;
