//Styles
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Tela from "../Tela.css"
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Form from "react-bootstrap/Form";
import React, { useContext, useEffect, useState } from 'react';
import ToggleButton from 'react-bootstrap/ToggleButton';
import ToggleButtonGroup from 'react-bootstrap/ToggleButtonGroup';
import Etiqueta from "../imagens/etiqueta.svg";

import Logo from "../imagens/quest_logo.png"


export default function Modal_Interesse(props) {

    //props.interessesToView([]);
    const [modalShow, setModalShow] = React.useState(false);

    const [interesse, setInteresse] = useState([]);
     const [interessesToView,setInteressesToView] = useState();

    useEffect(()=>{
        setInteresse([]);
        props.interessesToView([]);
    }, [])

    const submitInteresse = () => {
        props.interesses(interesse);
        props.interessesToView(interessesToView);
        props.loading(false);
        props.onHide(false);
    }


    const convertToObject = (interesse) => {
        //let interesses = [];
        for(let i = 0; i < interesse.length; i++){
            props.temas.map((tema) => {
                if(interesse[i] == tema.tema){
                    interesse[i] = tema;
                }
            })   
        }

        return interesse;
    }
    
  return (
    <div className="font_t">
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
      </Modal.Header>
      <Modal.Body>
        <h4 className="alinhamento titulo_login">Redefinir seus Interesses</h4>

        <div className="alinhamento">
        
        <Form.Group className="mb-2">
                
                {props.temas && props.temas.map((tema) => (
                    <ToggleButtonGroup  type="checkbox" className="mb-2 interesses" key={tema.id_tema} orientation="vertical">
                        
                        <ToggleButton 
                                className="mb-2 etiqueta_int"
                                id={tema.id_tema}
                                type="checkbox"
                                variant=""
                                checked={() => {interesse.includes(tema.tema); interessesToView.includes(tema)}}
                                name={tema.tema}
                                value={tema.tema}
                                onChange={(e) => {
                                    if (e.target.checked) { // add
                                        setInteresse([
                                        ...interesse, e.target.value
                                        ]);
                                        setInteressesToView([
                                        ...interessesToView, tema
                                        ])
                                    }else { // delete
                                        setInteresse(
                                        interesse.filter(x => 
                                            x !== e.target.value
                                        ))
                                        setInteressesToView(
                                          interessesToView.filter(x => 
                                            x !== tema
                                        ))
                                    }
                                    }}  
                            >
                                
                                   <img src={Etiqueta} alt="etiqueta"/> {tema.tema}
                                    
                                
                            </ToggleButton>
                    
                    </ToggleButtonGroup>
                        
                    
                ))}
              
        </Form.Group>


        </div>
        
      </Modal.Body>
      <Modal.Footer>
        <Button type='button' onClick={submitInteresse} className="botao">SALVAR ALTERAÇÕES</Button>
      </Modal.Footer>
    </Modal>
    </div>
  );
}
