    // }

    //estado=='Desbloq'

    //exibe
    //placa_desbloq.svg
    //clica
    //exibir pop D


    return (
        <div>
            <p></p>
            <Button > Exibe Koala Bloq </Button>
            <p></p>
            <Button > Exibe Urso Bloq </Button>
            <p></p>
            <Button > Exibe Koala Desbloq </Button>
            <p></p>
            <Button > Exibe Urso Desbloq </Button>
        </div>
    )
}


export default PlacasMasc;
