import React from 'react';
import Certificado from "../imagens/CertificadoQuests.png";

class PdfButton extends React.Component {
  // generatePdf = () => {
  //   // Receber o arquivo
  //   var receberArquivo = { Certificado }

  //   // Carregar a imagem
  //   var carregarImagem = receberArquivo[0];
  //   //console.log(carregarImagem);

  //   // FileReader - permite ler o conteudo do arquivo do computador do usuario
  //   var lerArquivo = new FileReader();

  //   // O evento onload ocorre quando um objeto é carregado
  //   lerArquivo.onload = function (arquivoCarregado) {
  //     // Converter a imagem para base64
  //     var imagemBase64 = arquivoCarregado.target.result;

  //     let doc = new window.jspdf.jsPDF();
  //     doc.text('Hello World!', 10, 10);
  //     doc.addImage(imagemBase64, 0, 0, 100, 100);
  //     doc.save('sample.pdf');
  //   }

  //   // O metodo readAsDataURL e usado para ler o conteudo
  //   lerArquivo.readAsDataURL(carregarImagem);


  // }

  loadImageAndCreatePdf = async () => {
    // URL da imagem
    const imageURL = {Certificado};
  
    // Fetch da imagem
    const response = await fetch(imageURL);
    const blob = await response.blob();
    
    // Cria um FileReader para ler o blob da imagem
    const reader = new FileReader();
    reader.onloadend = function () {
      // Cria uma nova instância do jsPDF
      const doc = new jsPDF();
    
      // Adiciona a imagem ao PDF
      doc.addImage(reader.result, "JPEG", 10, 10, 180, 160);
    
      // Salva o PDF
      doc.save("documento-com-imagem.pdf");
    }
  
    // Lê o blob da imagem como dataURL
    reader.readAsDataURL(blob);
  }

  render() {
    return (
      <button onClick={loadImageAndCreatePdf} className='botao botao_pdf p-2 pt-3'>Gerar PDF</button>
    );
  }
}

export default PdfButton;
