          console.log(error);

        });
  }


  const setter2 = (response) =>{
    if(response){
      props.page(2);    
    }
  }

  const setter3 = (response) =>{
    if(response){
      props.page(3);  
    }
  }

  return ( 
      <div className="background">
        <Header/>
        <Row className="justify-content-sm-center">
          <Col sm="auto" none="">
            <Form className="mb-5 rounded p-5 mx-3"  >

              {page == 1 ? <SendEmail email={(e) => setEmail(e.target.value)} send={sendEmail}/> : page == 2 ? <ConfirmCode /> : 
              <Reset3 cadastro={cadastroUsuario} redirect={redirect} navigate={navigate}/>}

