import React from 'react';
import Certificado from "../imagens/CertificadoQuests.jpg";
const criaCertificado = "https://smdquests.000webhostapp.com/api/reset/tabletop";

class PdfButton extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      buttonDisabled: false, // Inicialmente o botão está habilitado
      downloadLink: '' // Link temporário de download do PDF
    };
  }

  generatePdf = () => {
    // Acessando as props
    const user = this.props.user;
    const ch = this.props.ch;

    // Gerar data e hora atuais
    const now = new Date();
    const dateString1 = now.toLocaleDateString();
    const dateString2 = now.toLocaleDateString('pt-BR', {
      day: '2-digit',
      month: 'long',
      year: 'numeric'
    });

    const doc = new window.jspdf.jsPDF('landscape'); // Define a orientação como horizontal
    doc.addImage(Certificado, 'JPEG', 0, 0, 297.5, 210); // Adiciona a imagem ao PDF
    doc.text(user, 107, 74);
    doc.text(`${dateString1}`, 50, 105); // Adiciona a data e hora atuais ao PDF
    doc.text(ch, 175, 115);
    doc.text(`${dateString2}.`, 200, 130); // Adiciona a data e hora atuais ao PDF

    const pdfDataUri = doc.output('datauristring'); // Converte o PDF em uma URI de dados

    this.setState({ downloadLink: pdfDataUri }); // Atualiza o estado com o link de download
  }

  resetCh = async () => {
    this.generatePdf();
    const token = localStorage.getItem('token').replace(/["]/g, '');
    fetch(criaCertificado, {
      method: 'post',
      body: JSON.stringify({
        token
      })
    })
      .then(function (response) {
        return response.json();
      })
      .then(data => {
        if (data) {
          this.setState({ buttonDisabled: true }); // Desativa o botão
        } else {
          console.log('houve um erro');
        }
      })
      .catch(error => {
        // Lidar com erros
        console.error(error);
      });
  }

  render() {
    const { buttonDisabled, downloadLink } = this.state;

    return (
      <div>
        {downloadLink ? (
          <a href={downloadLink} download="certificado.pdf">
            <button
              className="botao botao_pdf p-2 botao_disable"
              disabled={buttonDisabled}
            >
              Baixar PDF
            </button>
          </a>
        ) : (
          <button
            onClick={this.resetCh}
            className={buttonDisabled ? 'botao botao_pdf p-2 botao_disable' : 'botao botao_pdf p-2 pt-3'}
            disabled={buttonDisabled}
          >
            Gerar PDF
          </button>
        )}
      </div>
    );
  }
}

export default PdfButton;
