//Styles
import "../Tela.css"
import ToggleButton from 'react-bootstrap/ToggleButton';
import ToggleButtonGroup from 'react-bootstrap/ToggleButtonGroup';
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Row from "react-bootstrap/esm/Row";
import Col from "react-bootstrap/esm/Col";

//Dependences
import {useFetch} from "../../hooks/useFetch";
import { useState , useEffect } from "react";



const Form2 = (props) => {   

    const [value, setValue] = useState([1, 3]);

    const handleChange = (val) => setValue(val);

    const [interesse, setInteresse] = useState([]);

    const submitInteresse = () => {
        props.interesses(interesse);
    }

    console.log(interesse)

    return <>
    <div className="font_popover">

            <h2 className="titulo_login">Quais seus interesses?</h2>
            <p className="">Essas informações vão nos ajudar a deixar o quest mais sua cara!</p>
            
            
            <ToggleButtonGroup type="checkbox" vertical value={value} onChange={handleChange}>
                <ToggleButton id="tbg-btn-1" value={1}>
                    Option 1
                </ToggleButton>
                <ToggleButton id="tbg-btn-2" value={2}>
                    Option 2
                </ToggleButton>
                <ToggleButton id="tbg-btn-3" value={3}>
                    Option 3
                </ToggleButton>
            </ToggleButtonGroup>
            
            
            <Form.Group className="mb-2">
                

                    {props.temas && props.temas.map((tema) => (
                        <ToggleButtonGroup  type="checkbox" className="mb-2" key={tema.id_tema} orientation="vertical">
                            
                            
                            <ToggleButton 
                                    className="mb-2"
                                    id={tema.id_tema}
                                    type="checkbox"
                                    variant="outline-primary"
                                    checked={interesse.includes(tema.tema)}
                                    name={tema.tema}
                                    value={tema.tema}
                                    onChange={(e) => {
                                        if (e.target.checked) { // add
                                            setInteresse([
                                            ...interesse, e.target.value
                                            ]);
                                        }else { // delete
                                            setInteresse(
                                            interesse.filter(x => 
                                                x !== e.target.value
                                            ))
                                        }
                                        }}  
                                >
                                    
                                        design
                                        
                                    
                                </ToggleButton>

                            
                            
                        </ToggleButtonGroup>
                            
                        
                    ))}
                

    
                    
            </Form.Group>
            <br/>
            <Button variant="primary" type="submit" onClick={submitInteresse} className="px-5 mb-3 mt-3 botao">
            FINALIZAR CADASTRO
            </Button>   
            </div>
    </>
}

export default Form2;
