import React from 'react';
import jsPDF from 'window.jspdf';
import Certificado from "../imagens/CertificadoQuests.png";

class PdfButton extends React.Component {
  generatePdf = () => {
    // Acessando as props
    const user = this.props.user;
    const ch = this.props.ch;

    // Você agora tem acesso aos valores de user e ch
    console.log(user, ch);

    const doc = new jsPDF('landscape'); // Define a orientação como horizontal
    const imgData = '../imagens/CertificadoQuests.png'; // Substitua pelo caminho da imagem desejada

    doc.text(user, 10, 10);
    doc.text(ch, 20, 10);
    doc.addImage(imgData, 'PNG', 10, 30, 150, 100); // Adiciona a imagem ao PDF
    doc.save('sample.pdf');
  }

  render() {
    return (
      <button onClick={this.generatePdf} className='botao botao_pdf p-2 pt-3'>Gerar PDF</button>
    );
  }
}

export default PdfButton;
