import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import "../components/Tela.css"
import Footer from "../components/Footer";
import Header from "../components/Header"
import { useEffect, useState } from "react";
import Form1 from "../components/Cadastro/Form1";
import Form2 from "../components/Cadastro/Form2";
const urlGet = "http://localhost/quests/temas";
const urlPost = "";
import {useFetch} from "../hooks/useFetch";

export default function Register() {


  function registerUsuario(e){
    e.preventDefault()
    console.log(`UsuÃ¡rio: ${email} Senha: ${password}`)
  }

  
  const [password1, setPassword1] = useState(1);
  const [password2, setPassword2] = useState(2);
  const [password, setPassword] = useState();
  
  if(password1 === password2){
    setPassword(password1);
  }
  
  const [nickname, setNickname] = useState();
  const [email, setEmail] = useState();
  const [name, setName] = useState();
  const [genero, setGenero] = useState();
  const [escolaridade, setEscolaridade] = useState();
  const [data_nascimento, setDataNascimento] = useState();
  const [interesses, setInteresses] = useState();
  

  const [form, setForm] = useState(1);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const user = {
      name,  
      nickname, 
      email, 
      password, 
      data_nascimento, 
      genero, 
      escolaridade,
    }

    httpConfig(user, "POST");


    setName('');
    setNickname('');
    setEmail('');
    setPassword('');
    setDataNascimento('');
    setGenero('');
    setEscolaridade('');
    setInteresses('');
  
  };






  const { data:temas} = useFetch(urlGet);
  const { data:user, httpConfig, loading, error} = useFetch(urlPost);


  return (
    <div className="background">
        
        <Header/>
      <Row className="justify-content-sm-center">
        <Col sm="auto" none="">
          <Form className="mb-4 rounded p-5 mx-3" onSubmit={registerUsuario} method="Post">
          {form === 1 ? <Form1 
          email={(e)=> setEmail(e.target.value)}
          password={(e)=> setPassword(e.target.value)}
          form={setForm}/> : <Form2 temas={temas} />}
          </Form>
            <Footer/>
        </Col>
      </Row>
    </div>
  );
}
